function [Chrom,ObjV] = immigrant(Chrom,ObjV)%%移民算子 %			变量名	类型	意义%输入参数	Chrom	Cell	每个元胞单元为一个种群的编码（移民前的）%			ObjV	Cell	每个元胞为一个种群所有个体的目标值（移民前的）%输出参数	Chrom	Cell	每个元胞单元为一个种群的编码（移民后的）%			ObjV	Cell	每个元胞为一个种群所有个体的目标值（移民后的）MP = length(Chrom);for i = 1 : MP	%找出第i种群中最优的个体	[MaxO,maxI] = max(ObjV{i});		%目标种群（移民操作中）	next_i = i+1;						if next_i> MP;		next_i = mod(next_i,MP);	end		%找出目标种群中最劣的个体	[MinO,minI] = min(ObjV{next_i});		%%目标种群最劣个体替换为源种群最优个体	Chrom{next_i}(minI,:) = Chrom{i}(maxI,:);	ObjV{next_i}(minI) = ObjV{i}(maxI);end