%[空运行环境clcclear%速度更新参数c1 = 1.49445; c2 = 1.49445;maxgen = 200;	%迭代次数sizepop = 20;	%种群规模%个体和速度最大最小值 popmax = 2;popmin = - 2;Vmax = 0.5;Vmin = -0.5;for i= 1:sizepop	%随机产生一个种群	pop(i,:) = 2*rands(1,2); 	% 初始化粒子	V(i,:) = 0.5*rands(1,2);	% 初始化速度		%计算粒子适应度值	fitness(i) = fun(pop(i,:));end[bestfitness,bestindex] = min(fitness); zbest = pop( bestindex,:);		%群体极值位Dgbest = pop;					%个体极值位置fitnessgbest = fitness;			%个体极值适应度值fitnesszbest = bestfitness;		%群体极值适应度值%迭代寻优 for i = 1 : maxgen	%粒子位置和速度更新 	for j=1:sizepop		%F度更新		V(j,:)=V(j,:)+c1*rand*(gbest(j,:)-pop(j,:))+c2*rand*(zbest-pop(j,:));		        V(j,find(V(j,:)>Vmax)) = Vmax;		V(j,find(V(j,:)<Vmin)) = Vmin;		%粒子更新		pop(j,:) = pop(j,:)+0.5*V(j,:);		        pop(j,find(pop(j,:)>popmax)) = popmax;		pop(j,find(pop(j,:)<popmin)) = popmin;		%新粒子适应度值		fitness(j) = fun(pop(j,:));	end	%个体极值和群体极值更新	for j = 1:sizepop		%个体极值(每个个体所经过的极值点)更新		if fitness(j) > fitnessgbest(j) 			gbest(j,:)= pop(j,:); 			fitnessgbest(j) = fitness(j);		end				%更新群体极值，更新个体		if fitness(j) > fitnesszbest 			zbest = pop(j,:);			fitnesszbest = fitness(j);		end	end	%每代最优值记录到yy数组中 	result(i) = fitnesszbest;end%输出最优值X  = zbestY = result(maxgen)%出每代最优个体适应度值 plot(result)title('最优个体适应度值','fontsize',12);xlabel('进化代数','fontsize',12);ylabel('适应度值','fontsize',12);